import { useLexicalComposerContext } from '@lexical/react/LexicalComposerContext';
import { useEffect } from 'react';
import { KEY_MODIFIER_COMMAND, COMMAND_PRIORITY_CRITICAL } from 'lexical';

export function ContentSelectAllPlugin(): JSX.Element | null {
  const [editor] = useLexicalComposerContext();

  useEffect(() => {
    const unregister = editor.registerCommand(
      KEY_MODIFIER_COMMAND,
      (event: KeyboardEvent) => {
        if ((event.ctrlKey || event.metaKey) && (event.key === 'a' || event.key === 'A')) {
          const content = document.querySelector('.a4-content');
          if (content === null) return false;
          event.preventDefault();
          const range = document.createRange();
          range.selectNodeContents(content);
          const sel = window.getSelection();
          if (sel === null) return true;
          sel.removeAllRanges();
          sel.addRange(range);
          return true; // handled
        }
        return false;
      },
      COMMAND_PRIORITY_CRITICAL
    );
    return unregister;
  }, [editor]);

  return null;
}

/**
 * ContentSelectAllPlugin
 *
 * Bu plugin, Ctrl+A (veya Mac'te Cmd+A) kısayoluna basıldığında sadece .a4-content alanındaki tüm içeriği seçer.
 * Böylece kullanıcı, sayfa içeriğini kolayca topluca seçebilir.
 *
 * Kullanım Senaryosu:
 * - Standart Ctrl+A davranışını override ederek, sadece içerik alanını seçmek için kullanılır.
 *
 * Notlar:
 * - Lexical editörün registerCommand API'si ile global kısayol dinlenir.
 * - DOM'da .a4-content class'ı ile işaretli alan hedeflenir.
 */

import { useLexicalComposerContext } from '@lexical/react/LexicalComposerContext';
import { useEffect } from 'react';
import { KEY_MODIFIER_COMMAND, COMMAND_PRIORITY_CRITICAL } from 'lexical';

export function ContentSelectAllPlugin(): JSX.Element | null {
  const [editor] = useLexicalComposerContext();

  useEffect(() => {
    // Ctrl+A veya Cmd+A tuş kombinasyonunu dinle.
    // Eğer .a4-content alanı varsa, tüm içeriği seç.
    const unregister = editor.registerCommand(
      KEY_MODIFIER_COMMAND,
      (event: KeyboardEvent) => {
        if ((event.ctrlKey || event.metaKey) && (event.key === 'a' || event.key === 'A')) {
          // .a4-content alanını bul
          const content = document.querySelector('.a4-content');
          if (content === null) return false;
          event.preventDefault();
          // Range ile tüm içeriği seç
          const range = document.createRange();
          range.selectNodeContents(content);
          const sel = window.getSelection();
          if (sel === null) return true;
          sel.removeAllRanges();
          sel.addRange(range);
          return true; // handled
        }
        return false;
      },
      COMMAND_PRIORITY_CRITICAL
    );
    return unregister;
  }, [editor]);

  return null;
}

import { useLexicalComposerContext } from '@lexical/react/LexicalComposerContext';
import { useEffect, useRef } from 'react';
import { $getRoot } from 'lexical';
import { updateAllHeaders, updateAllFooters } from '../utils/HeaderFooterSyncUtils';
import { $isPageNode } from '../nodes/PageNode';

/**
 * HeaderFooterSyncPlugin
 *
 * Bu plugin, header/footer edit modundayken bir header/footer değişikliği olduğunda
 * tüm sayfalardaki header/footer'ı otomatik olarak günceller.
 *
 * Kullanım Senaryosu:
 * - Kullanıcı bir sayfanın header veya footer'ını değiştirdiğinde, tüm sayfalarda aynı değişikliğin yansımasını sağlar.
 *
 * Notlar:
 * - updateAllHeaders ve updateAllFooters yardımcı fonksiyonlarını kullanır.
 * - Her güncellemede PageNode'larda header/footer'ın varlığını da garanti eder.
 */
export function HeaderFooterSyncPlugin(): JSX.Element | null {
  const [editor] = useLexicalComposerContext();
  const lastHeader = useRef<{ text: string; visible: boolean } | null>(null);
  const lastFooter = useRef<{ text: string; visible: boolean } | null>(null);

  useEffect(() => {
    // Lexical editörün state'i güncellendiğinde çalışır.
    // Header veya footer'da değişiklik olup olmadığını tespit eder.
    return editor.registerUpdateListener(({ editorState }) => {
      editorState.read(() => {
        const root = $getRoot();
        // Tüm sayfalardaki header/footer'ları topla
        const pageNodes = root.getChildren().filter($isPageNode);
        let changedHeader: { text: string; visible: boolean } | null = null;
        let changedFooter: { text: string; visible: boolean } | null = null;
        // Header değişikliği tespiti
        for (const page of pageNodes) {
          const header = page.getHeaderNode();
          if (header != null) {
            // Son header ile mevcut header karşılaştırılır
            if (
              lastHeader.current == null ||
              lastHeader.current.text !== header.__text ||
              lastHeader.current.visible !== header.__visible
            ) {
              changedHeader = { text: header.__text, visible: header.__visible };
              break;
            }
          }
        }
        // Footer değişikliği tespiti
        for (const page of pageNodes) {
          const footer = page.getFooterNode();
          if (footer != null) {
            // Son footer ile mevcut footer karşılaştırılır
            if (
              lastFooter.current == null ||
              lastFooter.current.text !== footer.__text ||
              lastFooter.current.visible !== footer.__visible
            ) {
              changedFooter = { text: footer.__text, visible: footer.__visible };
              break;
            }
          }
        }
        // Değişiklik yapılan header'ı tüm sayfalara uygula
        if (changedHeader != null) {
          const header = changedHeader; // TS için non-null
          editor.update(() => {
            updateAllHeaders(header.text, header.visible);
          });
          lastHeader.current = header;
        }
        // Değişiklik yapılan footer'ı tüm sayfalara uygula
        if (changedFooter != null) {
          const footer = changedFooter; // TS için non-null
          editor.update(() => {
            updateAllFooters(footer.text, footer.visible);
          });
          lastFooter.current = footer;
        }
        // Her güncellemede tüm PageNode'larda header/footer'ın varlığını garanti et
        editor.update(() => {
          pageNodes.forEach((page) => {
            if (typeof page.ensureHeaderFooterContentChildren === 'function') {
              page.ensureHeaderFooterContentChildren();
            }
          });
        });
      });
    });
  }, [editor]);

  return null;
}

import { useLexicalComposerContext } from '@lexical/react/LexicalComposerContext';
import { useEffect, useRef } from 'react';
import { $getRoot } from 'lexical';
import { updateAllHeaders, updateAllFooters } from '../utils/HeaderFooterSyncUtils';
import { $isPageNode } from '../nodes/PageNode';

/**
 * HeaderFooterSyncPlugin
 *
 * Bu plugin, header/footer edit modundayken bir header/footer değişikliği olduğunda
 * tüm sayfalardaki header/footer'ı otomatik olarak günceller.
 *
 * Kullanım Senaryosu:
 * - Kullanıcı bir sayfanın header veya footer'ını değiştirdiğinde, tüm sayfalarda aynı değişikliğin yansımasını sağlar.
 *
 * Notlar:
 * - updateAllHeaders ve updateAllFooters yardımcı fonksiyonlarını kullanır.
 * - Her güncellemede PageNode'larda header/footer'ın varlığını da garanti eder.
 */
export function HeaderFooterSyncPlugin(): JSX.Element | null {
  const [editor] = useLexicalComposerContext();
  const lastHeader = useRef<{ text: string; visible: boolean } | null>(null);
  const lastFooter = useRef<{ text: string; visible: boolean } | null>(null);

  useEffect(() => {
    // Lexical editörün state'i güncellendiğinde çalışır.
    // Header veya footer'da değişiklik olup olmadığını tespit eder.
