        // Tüm sayfalardaki header/footer'ları topla
        const pageNodes = root.getChildren().filter($isPageNode);
        let changedHeader: { text: string; visible: boolean } | null = null;
        let changedFooter: { text: string; visible: boolean } | null = null;
        // Header değişikliği tespiti
        for (const page of pageNodes) {
          const header = page.getHeaderNode();
          if (header != null) {
            // Son header ile mevcut header karşılaştırılır
            if (
              lastHeader.current == null ||
              lastHeader.current.text !== header.__text ||
              lastHeader.current.visible !== header.__visible
            ) {
              changedHeader = { text: header.__text, visible: header.__visible };
              break;
            }
          }
        }
        // Footer değişikliği tespiti
        for (const page of pageNodes) {
          const footer = page.getFooterNode();
          if (footer != null) {
            // Son footer ile mevcut footer karşılaştırılır
            if (
              lastFooter.current == null ||
              lastFooter.current.text !== footer.__text ||
              lastFooter.current.visible !== footer.__visible
            ) {
              changedFooter = { text: footer.__text, visible: footer.__visible };
              break;
            }
          }
        }
        // Değişiklik yapılan header'ı tüm sayfalara uygula
        if (changedHeader != null) {
          const header = changedHeader; // TS için non-null
          editor.update(() => {
            updateAllHeaders(header.text, header.visible);
            // Header değişikliği olduğunda header/footer varlığını garanti et
            pageNodes.forEach((page) => {
              if (typeof page.ensureHeaderFooterContentChildren === 'function') {
                page.ensureHeaderFooterContentChildren();
              }
            });
          });
          lastHeader.current = header;
        }
        // Değişiklik yapılan footer'ı tüm sayfalara uygula
        if (changedFooter != null) {
          const footer = changedFooter; // TS için non-null
          editor.update(() => {
            updateAllFooters(footer.text, footer.visible);
            // Footer değişikliği olduğunda header/footer varlığını garanti et
            pageNodes.forEach((page) => {
              if (typeof page.ensureHeaderFooterContentChildren === 'function') {
                page.ensureHeaderFooterContentChildren();
              }
            });
          });
          lastFooter.current = footer;
        }
      });
    });
  }, [editor]);

  return null;
}

import { $getRoot } from 'lexical';
import { $isPageNode } from '../nodes/PageNode';

/**
 * Tüm PageNode'ların header'ını verilen text ve visible ile günceller
 */
export function updateAllHeaders(text: string, visible: boolean): void {
  const root = $getRoot();
  root.getChildren().forEach((pageNode) => {
    if ($isPageNode(pageNode)) {
      const header = pageNode.getHeaderNode();
      if (header != null) {
        header.setText(text);
        header.setVisible(visible);
      }
    }
  });
}

/**
 * Tüm PageNode'ların footer'ını verilen text ve visible ile günceller
 */
export function updateAllFooters(text: string, visible: boolean): void {
  const root = $getRoot();
  root.getChildren().forEach((pageNode) => {
    if ($isPageNode(pageNode)) {
      const footer = pageNode.getFooterNode();
      if (footer != null) {
        footer.setText(text);
        footer.setVisible(visible);
      }
    }
  });
}

import { $getRoot } from 'lexical';
import { $isPageNode } from '../nodes/PageNode';

/**
 * HeaderFooterSyncUtils
 *
 * Bu dosya, tüm sayfalardaki header ve footer alanlarını topluca güncellemek için yardımcı fonksiyonlar içerir.
 *
 * updateAllHeaders: Tüm PageNode'ların header'ını verilen text ve visible ile günceller.
 * updateAllFooters: Tüm PageNode'ların footer'ını verilen text ve visible ile günceller.
 *
 * Kullanım Senaryosu:
 * - Bir header/footer değişikliği olduğunda, tüm sayfalarda aynı değerin yansımasını sağlamak için kullanılır.
 */

/**
 * Tüm PageNode'ların header'ını verilen text ve visible ile günceller
 */
export function updateAllHeaders(text: string, visible: boolean): void {
  // Root node'dan tüm PageNode'ları bulup, her birinin header'ını günceller.
  const root = $getRoot();
  root.getChildren().forEach((pageNode) => {
    if ($isPageNode(pageNode)) {
      const header = pageNode.getHeaderNode();
      if (header != null) {
        // Header'ın metni ve görünürlüğü güncellenir.
        header.setText(text);
        header.setVisible(visible);
      }
    }
  });
}

/**
 * Tüm PageNode'ların footer'ını verilen text ve visible ile günceller
 */
export function updateAllFooters(text: string, visible: boolean): void {
  // Root node'dan tüm PageNode'ları bulup, her birinin footer'ını günceller.
  const root = $getRoot();
  root.getChildren().forEach((pageNode) => {
    if ($isPageNode(pageNode)) {
      const footer = pageNode.getFooterNode();
      if (footer != null) {
        // Footer'ın metni ve görünürlüğü güncellenir.
        footer.setText(text);
        footer.setVisible(visible);
      }
    }
  });
}

import { useLexicalComposerContext } from '@lexical/react/LexicalComposerContext';
import { $getRoot } from 'lexical';
import { useEffect } from 'react';
import { PageNode } from '../nodes/PageNode';

export function PageInitializerPlugin(): JSX.Element | null {
  const [editor] = useLexicalComposerContext();

  useEffect(() => {
    editor.update(() => {
      const root = $getRoot();
      if (root.getChildrenSize() === 0) {
        const page = new PageNode({});
        page.appendInitialChildren(); // Always add header/footer/content (header/footer hidden by default)
        root.append(page);
      }
    });
  }, [editor]);

  return null;
}

import { useLexicalComposerContext } from '@lexical/react/LexicalComposerContext';
import { $getRoot } from 'lexical';
