import { useLexicalComposerContext } from '@lexical/react/LexicalComposerContext';
import { useEffect } from 'react';
import { $getRoot } from 'lexical';

export function WordCountPlugin({
  onWordCountChange
}: {
  onWordCountChange?: (words: number, chars: number) => void;
}): null {
  const [editor] = useLexicalComposerContext();

  useEffect(() => {
    return editor.registerUpdateListener(() => {
      editor.getEditorState().read(() => {
        const text = $getRoot().getTextContent();
        const words = text.trim().split(/\s+/).filter((word) => word.length > 0).length;
        const chars = text.length;
        onWordCountChange?.(words, chars);
      });
    });
  }, [editor, onWordCountChange]);

  return null;
}

import { useLexicalComposerContext } from '@lexical/react/LexicalComposerContext';
import { $getRoot } from 'lexical';
import { useEffect, useCallback } from 'react';
import { $createPageBreakNode, $isPageBreakNode, type PageBreakNode } from './PageBreakNode';

export interface PaginationSettings {
  pageHeight: number; // mm
  pageWidth: number; // mm
  marginTop: number; // mm
  marginBottom: number; // mm
  marginLeft: number; // mm
  marginRight: number; // mm
}

export const DEFAULT_PAGINATION_SETTINGS: PaginationSettings = {
  pageHeight: 297, // A4 height
  pageWidth: 210, // A4 width
  marginTop: 25,
  marginBottom: 25,
  marginLeft: 20,
  marginRight: 20
};

export const PAGINATION_PRESETS: Record<string, Partial<PaginationSettings>> = {
  A4: { pageWidth: 210, pageHeight: 297 },
  A3: { pageWidth: 297, pageHeight: 420 },
  Letter: { pageWidth: 216, pageHeight: 279 },
  Legal: { pageWidth: 216, pageHeight: 356 }
};

export function PaginationPlugin({
  settings = DEFAULT_PAGINATION_SETTINGS,
  onPageCountChange
}: {
  settings?: Partial<PaginationSettings>;
  onPageCountChange?: (pageCount: number) => void;
}): null {
  const [editor] = useLexicalComposerContext();
  const finalSettings = { ...DEFAULT_PAGINATION_SETTINGS, ...settings };
  const contentHeight =
    finalSettings.pageHeight - finalSettings.marginTop - finalSettings.marginBottom;

  const checkForPageBreak = useCallback(() => {
    editor.update(() => {
      const root = $getRoot();
      const children = root.getChildren();
      let currentPageHeight = 0;
      let currentPageNumber = 1;
      const pageBreaksToAdd: Array<{ index: number; pageNumber: number }> = [];
      const existingPageBreaks: PageBreakNode[] = [];

      // Collect existing page breaks
      children.forEach((child) => {
        if ($isPageBreakNode(child)) {
          existingPageBreaks.push(child);
        }
      });

      // Remove all existing page breaks
      existingPageBreaks.forEach((pageBreak) => {
        pageBreak.remove();
      });

      // Recalculate page breaks
      const updatedChildren = root.getChildren();
      updatedChildren.forEach((child, index) => {
        if (!$isPageBreakNode(child)) {
          const element = editor.getElementByKey(child.getKey());
          if (element !== null) {
            const elementHeight = element.offsetHeight;
            const elementHeightMm = (elementHeight * 25.4) / 96; // Convert px to mm

            if (currentPageHeight + elementHeightMm > contentHeight) {
              pageBreaksToAdd.push({ index, pageNumber: currentPageNumber + 1 });
              currentPageNumber++;
              currentPageHeight = elementHeightMm;
            } else {
              currentPageHeight += elementHeightMm;
            }
          }
        }
      });

      // Add page breaks
      pageBreaksToAdd.reverse().forEach(({ index, pageNumber }) => {
        const nodeAtIndex = root.getChildAtIndex(index);
        if (nodeAtIndex !== null) {
          const pageBreak = $createPageBreakNode(pageNumber);
          nodeAtIndex.insertBefore(pageBreak);
        }
      });

      // Update page count
      const newPageCount = Math.max(1, currentPageNumber);
      onPageCountChange?.(newPageCount);
    });
  }, [editor, contentHeight, onPageCountChange]);

  useEffect(() => {
    const timer = setTimeout(() => {
      checkForPageBreak();
    }, 500);

    return () => {
      clearTimeout(timer);
    };
  }, [checkForPageBreak]);

  useEffect(() => {
    return editor.registerUpdateListener(({ dirtyElements, dirtyLeaves }) => {
      if (dirtyElements.size > 0 || dirtyLeaves.size > 0) {
        setTimeout(() => {
          checkForPageBreak();
        }, 100);
      }
    });
  }, [editor, checkForPageBreak]);

  return null;
}

import { useLexicalComposerContext } from '@lexical/react/LexicalComposerContext';
import { $getRoot } from 'lexical';
import { useEffect, useCallback } from 'react';
import { $createPageBreakNode, $isPageBreakNode, type PageBreakNode } from './PageBreakNode';
import { PaginationSettings, DEFAULT_PAGINATION_SETTINGS, PAGINATION_PRESETS } from './PaginationSettings';

export function PaginationPlugin({
  settings = DEFAULT_PAGINATION_SETTINGS,
  onPageCountChange
}: {
  settings?: Partial<PaginationSettings>;
  onPageCountChange?: (pageCount: number) => void;
}): null {
  const [editor] = useLexicalComposerContext();
  const finalSettings = { ...DEFAULT_PAGINATION_SETTINGS, ...settings };
  const contentHeight =
    finalSettings.pageHeight - finalSettings.marginTop - finalSettings.marginBottom;

  const checkForPageBreak = useCallback(() => {
    editor.update(() => {
      const root = $getRoot();
      const children = root.getChildren();
      let currentPageHeight = 0;
      let currentPageNumber = 1;
      const pageBreaksToAdd: Array<{ index: number; pageNumber: number }> = [];
      const existingPageBreaks: PageBreakNode[] = [];

      // Collect existing page breaks
      children.forEach((child) => {
        if ($isPageBreakNode(child)) {
          existingPageBreaks.push(child);
        }
      });

      // Remove all existing page breaks
      existingPageBreaks.forEach((pageBreak) => {
        pageBreak.remove();
      });

      // Recalculate page breaks
      const updatedChildren = root.getChildren();
      updatedChildren.forEach((child, index) => {
        if (!$isPageBreakNode(child)) {
          const element = editor.getElementByKey(child.getKey());
          if (element !== null) {
            const elementHeight = element.offsetHeight;
            const elementHeightMm = (elementHeight * 25.4) / 96; // Convert px to mm

            if (currentPageHeight + elementHeightMm > contentHeight) {
              pageBreaksToAdd.push({ index, pageNumber: currentPageNumber + 1 });
              currentPageNumber++;
              currentPageHeight = elementHeightMm;
            } else {
              currentPageHeight += elementHeightMm;
            }
          }
        }
      });

      // Add page breaks
      pageBreaksToAdd.reverse().forEach(({ index, pageNumber }) => {
        const nodeAtIndex = root.getChildAtIndex(index);
        if (nodeAtIndex !== null) {
          const pageBreak = $createPageBreakNode(pageNumber);
          nodeAtIndex.insertBefore(pageBreak);
        }
      });

      // Update page count
      const newPageCount = Math.max(1, currentPageNumber);
      onPageCountChange?.(newPageCount);
    });
  }, [editor, contentHeight, onPageCountChange]);

  useEffect(() => {
    const timer = setTimeout(() => {
      checkForPageBreak();
    }, 500);

    return () => {
      clearTimeout(timer);
    };
  }, [checkForPageBreak]);

  useEffect(() => {
    return editor.registerUpdateListener(({ dirtyElements, dirtyLeaves }) => {
      if (dirtyElements.size > 0 || dirtyLeaves.size > 0) {
        setTimeout(() => {
          checkForPageBreak();
        }, 100);
      }
    });
  }, [editor, checkForPageBreak]);

  return null;
}

import React, { useState } from 'react';
import type { PaginationSettings } from './PaginationPlugin';

interface PaginationSettingsProps {
  settings: PaginationSettings;
  onSettingsChange: (settings: PaginationSettings) => void;
  isVisible: boolean;
  onToggle: () => void;
}

export function PaginationSettingsPanel({
  settings,
  onSettingsChange,
  isVisible,
  onToggle
}: PaginationSettingsProps): JSX.Element {
  const [localSettings, setLocalSettings] = useState(settings);

  const handleChange = (field: keyof PaginationSettings, value: number): void => {
    const newSettings = { ...localSettings, [field]: value };
    setLocalSettings(newSettings);
    onSettingsChange(newSettings);
  };

  const presets = {
    A4: { pageWidth: 210, pageHeight: 297 },
    A3: { pageWidth: 297, pageHeight: 420 },
    Letter: { pageWidth: 216, pageHeight: 279 },
    Legal: { pageWidth: 216, pageHeight: 356 }
  };

  const applyPreset = (preset: keyof typeof presets): void => {
    const newSettings = {
      ...localSettings,
      ...presets[preset]
    };
    setLocalSettings(newSettings);
    onSettingsChange(newSettings);
  };

  if (!isVisible) {
    return (
      <button className="pagination-control-btn settings-toggle" onClick={onToggle}>
        ⚙️
      </button>
    );
  }

  return (
    <div className="page-settings">
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <h3>Sayfa Ayarları</h3>
        <button
          onClick={onToggle}
          style={{
            background: 'none',
            border: 'none',
            cursor: 'pointer',
            fontSize: '16px'
          }}
        >
          ✕
        </button>
      </div>

      <div style={{ marginBottom: '12px' }}>
        <label>Sayfa Boyutu Önayarları:</label>
        <div style={{ display: 'flex', gap: '4px', marginTop: '4px' }}>
          {Object.keys(presets).map((preset) => (
            <button
              key={preset}
              onClick={() => {
                applyPreset(preset as keyof typeof presets);
              }}
              style={{
                padding: '4px 8px',
                border: '1px solid #d1d1d1',
                borderRadius: '2px',
                background: 'white',
                cursor: 'pointer',
                fontSize: '10px'
              }}
            >
              {preset}
            </button>
          ))}
        </div>
      </div>

      <label>
        Genişlik (mm):
        <input
          type="number"
          value={localSettings.pageWidth}
          onChange={(e) => {
            handleChange('pageWidth', Number(e.target.value));
          }}
          min="100"
          max="500"
        />
      </label>

      <label>
        Yükseklik (mm):
        <input
          type="number"
          value={localSettings.pageHeight}
          onChange={(e) => {
            handleChange('pageHeight', Number(e.target.value));
          }}
          min="100"
          max="700"
        />
      </label>

      <label>
        Üst Kenar Boşluğu (mm):
        <input
          type="number"
          value={localSettings.marginTop}
          onChange={(e) => {
            handleChange('marginTop', Number(e.target.value));
          }}
          min="5"
          max="50"
        />
      </label>

      <label>
        Alt Kenar Boşluğu (mm):
        <input
          type="number"
          value={localSettings.marginBottom}
          onChange={(e) => {
            handleChange('marginBottom', Number(e.target.value));
          }}
          min="5"
          max="50"
        />
      </label>

      <label>
        Sol Kenar Boşluğu (mm):
        <input
          type="number"
          value={localSettings.marginLeft}
          onChange={(e) => {
            handleChange('marginLeft', Number(e.target.value));
          }}
          min="5"
          max="50"
        />
      </label>

      <label>
        Sağ Kenar Boşluğu (mm):
        <input
          type="number"
          value={localSettings.marginRight}
          onChange={(e) => {
            handleChange('marginRight', Number(e.target.value));
          }}
          min="5"
          max="50"
        />
      </label>

      <div style={{ marginTop: '12px', fontSize: '10px', color: '#666' }}>
        Ctrl+Enter: Yeni sayfa ekle
      </div>
    </div>
  );
}

export interface PaginationSettings {
  pageHeight: number; // mm
  pageWidth: number; // mm
  marginTop: number; // mm
  marginBottom: number; // mm
  marginLeft: number; // mm
  marginRight: number; // mm
}

export const DEFAULT_PAGINATION_SETTINGS: PaginationSettings = {
  pageHeight: 297, // A4 height
  pageWidth: 210,  // A4 width
  marginTop: 25,
  marginBottom: 25,
  marginLeft: 20,
  marginRight: 20
};

export const PAGINATION_PRESETS: Record<string, Partial<PaginationSettings>> = {
  A4: { pageWidth: 210, pageHeight: 297 },
  A3: { pageWidth: 297, pageHeight: 420 },
  Letter: { pageWidth: 216, pageHeight: 279 },
  Legal: { pageWidth: 216, pageHeight: 356 }
};

