      if (!pageNode) return;
      const footerNode = pageNode.getFooterNode();
      if (!footerNode) return;
      footerNode.setVisible(!footerNode.isVisible());
    });
  };

  return (
    <div className="toolbarGroup">
      <ToolbarButton
        onClick={() => {
          setHeaderFooterEditMode(!headerFooterEditMode);
