    </div>
  );
}

export function ToolbarPlugin({
  headerFooterEditMode,
  setHeaderFooterEditMode,
}: ToolbarPluginProps): JSX.Element {
  return (
    <Toolbar.Root className="toolbarRoot">
      <PageSectionPlugin
        headerFooterEditMode={headerFooterEditMode}
