import { ElementNode, SerializedElementNode, EditorConfig, Spread } from 'lexical';
import * as React from 'react';

export type SerializedSectionNode = Spread<
  {
    type: 'section';
    version: 1;
  },
  SerializedElementNode
>;

export class SectionNode extends ElementNode {
  static getType(): string {
    return 'section';
  }

  static clone(node: SectionNode): SectionNode {
    return new SectionNode(node.__key);
  }

  createDOM(_config: EditorConfig): HTMLElement {
    const dom = document.createElement('section');
    dom.className = 'a4-section';
    return dom;
  }

  updateDOM(): boolean {
    return false;
  }

  static importJSON(_serializedNode: SerializedSectionNode): SectionNode {
    return new SectionNode();
  }

  exportJSON(): SerializedSectionNode {
    return {
      ...super.exportJSON(),
      type: 'section',
      version: 1,
    };
  }

  decorate(): JSX.Element | null {
    return null;
  }
}

